import React, { Component } from 'react'
import Select from 'react-select'
import countryList from 'react-select-country-list'
import SelectCurrency from 'react-select-currency'
import styles from './FormStyle.css'
import axios from 'axios'


const cList = countryList().getData()
const selectedCurrency = currency => {
    //this.state.currency = currency
    console.log(currency)
    this.setState({currency})
}
const formErrors ={
    merchantError: '',
    merchantNameError:'',
    merchant_passwordError:'',
    orderIdError:'',
    productError:'',
    priceError:'',
    currencyError: '',
    quantityError:'',
    customError: '',
    amountError:'',
    countryError: '',
    firstnameError:'',
    lastnameError:'',
    addressError: '',
    cityError: '',
    stateCError:'',
    postalcodeError: '',
    mobileError: '',
    emailError:'',
    customer_accError:'',
    customer_idError:''
}
export default class Form extends Component{
    constructor(props) {
    super(props);
    this.state = 
    {
        merchant: '',
        mercaht_name: '',
        merchant_password:'',
        order_id:'',
        customer_id:'',
        item_name:'',
        item_price:'',
        currency_: '',
        quantity:'',
        custom: '',
        amount:'',
        country: '',
        user_firstname:'',
        user_lastname:'',
        user_email: '',
        user_mobile: '',

    }
    }
    isValid = (formErrors) => {
        let valid = false;
        Object.keys(formErrors).values.length > 0 ? valid = false : valid = true;
        return valid;
    }
    handleError=(e)=>{
        console.log("Handle error function called")
        console.log(formErrors)
    }
    handleSubmit=(event)=>{
        event.preventDefault();
        console.log("handleSummit method ")
        if(this.isValid(formErrors)){
            const paymentData = {
                merchant : { //"merchantAccount":"mearaftadewos@gmail.com"
                    merchantAccount: this.state.merchant,                    
                },
                order:{ //"amount":10,"orderId":"test1234","item_name":"test","item_price":"10","quantity":"1","currency":"ETB"
                    amount:this.state.amount,
                    orderId: this.state.orderId,
                    item_name:this.state.item_name,
                    item_price:this.state.item_price,
                    custom: this.state.custom,
                    quantity:this.state.quantity,
                    currency: this.state.currency,
                },
                payer:{ //"country":"KE","firstName":"First Name","lastName":"LastName","mobile":"+123456789","email":"buyer@buyer.com
                    country: this.state.country,
                    firstname: this.state.user_firstname,
                    lastname:this.state.user_lastname,
                    mobile: this.state.mobile,
                    email: this.state.email,
                }
            }
            console.log(paymentData)
            const url=`https://sandbox.flocash.com/ecom/ecommerce.do`
            const token = Buffer.from(`${this.state.merchant_name}:${this.state.merchant_password}`, 'utf8').toString('base64')
            axios.post(url, paymentData, {headers: {'Authorization': `Basic ${token}`}} )
                .then(res=>{
                    console.log(res)
                    console.log(res.data)
                }).catch(error => {
                        console.error('Catch an error!', error.message);
                    });
        }   
        else{
            this.handleError(event)
        }
    }

    handleChange=(e)=>{
        e.preventDefault();
        const emailRegExp = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
        const passwordRegExp = RegExp(/^(?=.*\d)(?=.*[a-z])(?=.&[A-Z])[0-9a-zA-Z]{8,}$/)
        const currencyRegex = RegExp(/^(?=.*\d)(?=.&[A-Z])[A-Z]{3,}$/)
        const nameRegExp = RegExp(/^(?=.*\d)(?=.*[a-z])(?=.&[A-Z])[A-Z]{3,}$/)
        const numberRegExp = RegExp(/^(?=.*[0-9]){1,}$/)
        const mobileRegExp = RegExp(/^(\+[1-9]){1}[1-9]{1}[0-9]{9,14}$/)

        const {name, value} = e.target;
        switch(name){
            case "merchant": 
                formErrors.merchantError = emailRegExp.test(value) || value.length < 6 ? "Merchant account should be valid email address" : ""; this.state.merchant = value;
            break;
            case "merchant_name": 
                formErrors.merchantNameError = nameRegExp.test(value) || value.length < 6 ? "minmum 6 charachters" :""; this.state.merchant_name = value;
            break;
            case "merchant_password": 
                formErrors.merchant_passwordError = passwordRegExp.test(value) || value.length < 8 ? "minmum 8 charachters" : ""; this.state.merchant_password = value;
            break;
            case "order_id": 
            formErrors.orderIdError = value.length < 4 ? "minmum 4 charachters" : ""; this.state.order_id = value
            break;
            case "customer_id": 
                formErrors.customer_idError = value.length < 4 ? "minmum 4 charachters" : ""; this.state.customer_id = value
            break;
            case "item_name": 
                formErrors.productError = value.length < 3 ? "minmum 3 charachters" : ""; this.state.item_name = value;
            break;
            case "item_price": 
                formErrors.priceError = numberRegExp.test(value) || value.lenght < 1 ? "enter only numbers" : ""; this.state.item_price = value;
            break;
            case "currency": 
                 formErrors.currencyError = currencyRegex.test(value) || value.length == 2 ? "maximum 2 upercased charachters" : ""; this.state.currency_code = value;
            break;
            case "quantity": 
                formErrors.quantityError = numberRegExp.test(value) || value.length < 1 ? "only numbers" : ""; this.state.quantity = value;
            break;
            case "custom": 
                 formErrors.customError = value.length < 4 ? "minmum 4 charachters" : this.state.custom = value;
            break;
            case "amount": 
                formErrors.amountError = numberRegExp.test(value) || value.length < 1 ? "only numbers" : ""; this.state.amount = value;
            break;
            case "customer_acc": 
                formErrors.customer_acc = emailRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" : ""; this.state.customer_acc = value;
            break;
            case "country": 
                formErrors.countryError = value.length = 2 ? "2 upper cased charachters" :""; this.state.country = value;
            break;

            case "user_firstname": 
                formErrors.firstnameError = nameRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" :""; this.state.user_firstname = value;
            break;
            case "user_lastname": 
                formErrors.lastnameError = nameRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" :""; this.state.user_lastname = value;
            break;
            case "user_address": 
                formErrors.addressError = nameRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" : ""; this.state.user_address = value;
            break;
            case "user_city": 
                formErrors.cityError = nameRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" : ""; this.state.user_city = value;
            break;
            case "user_state": 
                formErrors.stateCError = nameRegExp.test(value) && value.length < 3 ? "minmum 4 digit number" : ""; this.state.user_state = value;
            break;
            case "user_postalcode": 
                formErrors.postalcodeError = numberRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" : ""; this.state.user_postalcode = value;
            break;
            case "user_email": 
                formErrors.email = emailRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" : ""; this.state.user_email = value;
            break;
            case "user_mobile": 
            formErrors.mobile = mobileRegExp.test(value) && value.length < 3 ? "invalid mobile number" : ""; this.state.user_mobile = value;
            break;

            default: 
            break;
        } 
    }
    selectedCurrency = (currency) => {
        this.state.currency = currency;
    }
    handleSelectChange = country => {
        this.state.country=country.value
        this.setState({ country });
        console.log(`Option selected:`, country);
    }

    render(){
        const country = this.state.country;
        return (

            <div className="form-content">
               <form className="form" action="https://sandbox.flocash.com/ecom/ecommerce.do`">
                    <h2 className="headline">Express Checkout </h2>
                    <h3 className="info">Merchant Info</h3>
                    <div>
                        <label htmlFor="merchant" className="form-lable">
                            <input type="email" name="merchant" className="form-input" required="true" placeholder="Merchant Account" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.merchantError != null ?(
                        <span className="from-error">{formErrors.merchantError}</span>
                       ):
                       null
                   }
                   <div>
                        <label htmlFor="merchant_name" className="form-lable">
                            <input type="text" name="merchant_name" className="form-input" required="true" placeholder="API User Name" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.merchantNameError != null ?(
                        <span className="from-error">{formErrors.merchantNameError}</span>
                       ):
                       null
                   }
                    <div>
                        <label htmlFor="merchant_password" className="form-lable">
                            <input type="password" name="merchant_password" className="form-input" required="true" placeholder="API User Password" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.merchant_passwordError != null ?(
                        <span className="from-error">{formErrors.merchant_passwordError}</span>
                       ):
                       null
                   }

                   <h3 className="info">Order Info</h3>

                   <div>
                        <label htmlFor="orderId" className="form-lable">
                            <input type="text" name="order_id" className="form-input" required="true" placeholder="Order Id" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.orderIdError != null ? (
                        <div className="from-error">{formErrors.orderIdError}</div>
                       ):
                       null
                   }

                   <div >
                        <label htmlFor="iteme_name" className="form-lable">
                            <input type="text" name="item_name" className="form-input" required="true" placeholder="Product" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.productError !=null ?(
                        <div className="from-error">{formErrors.productError}</div>
                       ):
                       null
                   }

                   <div>
                        <label htmlFor="item_price" className="form-lable">
                            <input type="number" name="item_price" className="form-input" required="true" placeholder="Price" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.priceError != null ?(
                        <div className="from-error">{formErrors.priceError}</div>
                       ):
                       null
                   }
                   <div>
                        <label htmlFor="quantity" className="form-lable">
                            <input type="number" name="quantity" className="form-input" required="true" placeholder="Quantity" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.quantityError != null ?(
                        <div className="from-error">{formErrors.quantityError}</div>
                       ):
                       null
                   }

                   <div>
                        <label htmlFor="custom" className="form-lable">
                            <input type="text" name="custom" className="form-input" required="true" placeholder="Custom ID" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.customError != null ?(
                        <div className="from-error">{formErrors.customError}</div>
                       ):
                       null
                   }

                   <div>
                        <label htmlFor="amount" className="form-lable">
                            <input type="number" name="amount" className="form-input" required="true" placeholder="Amount" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.amountError != null ?(
                        <div className="from-error">{formErrors.amountError}</div>
                       ):
                       null
                   }
                    <div className="form-input-div">
                        <label htmlFor="currency" className="form-lable">  
                            Currency:
                            <SelectCurrency value={'USD'} onCurrencySelected={selectedCurrency} />

                        </label>
                   </div>
                   {
                       formErrors.currencyError != null?(
                        <div className="from-error">{formErrors.currencyError}</div>
                       ):
                       null
                   }
                   <div >
                        <label htmlFor="customer_acc" className="form-lable">

                        </label>
                    </div>


                    <h3 className="info">Payer Info</h3>

                    <div className="form-input-div">

                        <label htmlFor="country" className="form-lable">
                        <Select placeholder="---- select country ----" options={cList} value={this.state.country} onChange={this.handleSelectChange}/>
                        </label>
                    </div>
                    {
                        formErrors.countryError != null ?(
                        <div className="from-error">{formErrors.countryError}</div>
                        ):
                        null
                    }
                    <div>
                        <label htmlFor="user_firstname" className="form-lable">
                            <input type="text" name="user_firstname" className="form-input" required="true" placeholder="Firstname" onChange={this.handleChange}/>
                        </label>
                    </div>
                    {
                        formErrors.firstnameError != null ?(
                        <div className="from-error">{formErrors.firstnameError}</div>
                        ):
                        null
                    }

                    <div>
                        <label htmlFor="user_lastname" className="form-lable">
                            <input type="text" name="user_lastname" className="form-input" required="true" placeholder="Lastname" onChange={this.handleChange}/>
                        </label>
                    </div>
                    {
                        formErrors.lastnameError != null ?(
                        <div className="from-error">{formErrors.lastnameError}</div>
                        ):
                        null
                    }
                    <div>
                        <label htmlFor="user_mobile" className="form-lable">
                            <input type="tel" name="user_mobile" className="form-input" required="true" placeholder="Mobile" onChange={this.handleChange}/>
                        </label>
                    </div>
                    {
                        formErrors.mobileError != null?(
                        <div className="from-error">{formErrors.mobileError}</div>
                        ):
                        null
                    }

                    <div >
                        <label htmlFor="user_email" className="form-lable">
                            <input type="email" name="user_email" className="form-input" required="true" placeholder=" Email" onChange={this.handleChange}/>
                        </label>
                    </div>
                    {
                        formErrors.emailError != null ?(
                        <div className="from-error">{formErrors.emailError}</div>
                        ):
                        null
                    }                   


                   <button className="form-input-btn" type="submit" onSubmit={this.handleSubmit}>Checkout</button>
                </form>

            </div>
        )
    }


}
