import React,{Component, isValidElement} from 'react'
import Select from 'react-select'
import countryList from 'react-select-country-list'
import SelectCurrency from 'react-select-currency'



const cList = countryList().getData()
const selectedCurrency = currency => {
    this.setState({currency})
}

export default class FormCheckout extends Component{
    state={
        mearachant : '',
        apiUsername:'',
        apiUserPassword:'',

        orderId:'',
        product:'',
        price:'',
        currency: '',
        quantity:'',
        custom: '',
        amount:'',

        country: null,
        firstname:'',
        lastname:'',
        mobile: '',
        email: '',
        //-------------------
        mearachantError: '',
        apiUsernameError:'',
        apiUserPasswordError:'',

        orderIdError:'',
        productError:'',
        priceError:'',
        currencyError: '',
        quantityError:'',
        customError: '',
        amountError:'',

        countryError: null,
        firstnameError:'',
        lastnameError:'',
        mobileError: '',
        emailError:'',


    }

    validate = () =>{

    }

    handleSubmit=event=>{
        const isValid = this.validate
        event.preventDefault();
        console.log(this.state)
    }

    handleChange=(e)=>{
        this.setState({
            [e.target.id]:e.target.value
        },()=>{
            console.log(this.state);
        })

    }

    selectedCurrency = currency => {
        this.setState({ currency });
    }

    handleSelectChange = country => {
        this.setState({ country });
        console.log(`Option selected:`, country);
      };


    render(){
        const { country } = this.state;
        return (
            <div className="form-content-right">
               <form className="form"  onSubmit={this.handleSubmit}>
                   <h1> Express Check out </h1>


                    <h3>Merchant Info</h3>

                   <div className="form-input">
                        <label htmlFor="merchant" className="form-lable">
                            <input type="text" name="merchant" className="form-input" required="true" placeholder="Merchant" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       this.setState.mearachantError?(
                        <div className="from-error">{this.state.mearachantError}</div>
                       ):
                       null
                   }

                   <div className="form-input">
                        <label htmlFor="apiUsername" className="form-lable">
                            <input type="text" name="apiUsername" className="form-input" required="true" placeholder="Api Username" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       this.setState.mearachantError?(
                        <div className="from-error">{this.state.apiUsernameError}</div>
                       ):
                       null
                   }

                   <div className="form-input">
                        <label htmlFor="apiUserPassword" className="form-lable">
                            <input type="password" name="apiUserPassword" className="form-input" required="true" placeholder="API User password" onChange={this.handleChange}/>
                        </label>
                   </div>  
                   {
                       this.setState.mearachantError?(
                        <div className="from-error">{this.state.apiUserPasswordError}</div>
                       ):
                       null
                   }          


                   <h3>Order Info</h3>

                   <div className="form-input">
                        <label htmlFor="orderId" className="form-lable">
                            <input type="text" name="orderId" className="form-input" required="true" placeholder="Order Id" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       this.setState.mearachantError?(
                        <div className="from-error">{this.state.orderIdError}</div>
                       ):
                       null
                   }

                   <div className="form-input">
                        <label htmlFor="product" className="form-lable">
                            <input type="text" name="product" className="form-input" required="true" placeholder="Product" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       this.setState.mearachantError?(
                        <div className="from-error">{this.state.productError}</div>
                       ):
                       null
                   }

                   <div className="form-input">
                        <label htmlFor="price" className="form-lable">
                            <input type="text" name="price" className="form-input" required="true" placeholder="Price" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       this.setState.mearachantError?(
                        <div className="from-error">{this.state.priceError}</div>
                       ):
                       null
                   }

                   <div className="form-input">
                        <label htmlFor="currency" className="form-lable">
                            <SelectCurrency value={'USD'} onCurrencySelected={selectedCurrency} />

                        </label>
                   </div>
                   {
                       this.setState.mearachantError?(
                        <div className="from-error">{this.state.customError}</div>
                       ):
                       null
                   }

                   <div className="form-input">
                        <label htmlFor="quantity" className="form-lable">
                            <input type="text" name="quantity" className="form-input" required="true" placeholder="Quantity" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       this.setState.mearachantError?(
                        <div className="from-error">{this.state.quantityError}</div>
