                   </div>
                   {
                       this.setState.mearachantError?(
                        <div className="from-error">{this.state.emailError}</div>
                       ):
                       null
                   }

                   <p>Please make sure you have filled the required information</p>

                   <button className="form-input-btn" type="submit">Checkout</button>
                </form>

            </div>
