import {useState, useEffect}from 'react'

const hookForm = ()=>{
    const [values, setValues] = useState({
        username : '',
        email: '',
        passowrd: '',
        confirmPassword: '',
    })
    const [errors, setErrors] = useState({});

    handleChange = e => {
        const {name, value} = e.targate
        setValues({
            ...values,
            [name]: value
        });
    }

    return {handleChange, values};
}

export default hookForm;
