import {useState, useEffect}from 'react'

const helpForm = ()=>{

    //const [errors, setErrors] = useState({});

    const handleChange = (e) => {
        const name = e.targate.name
        const value = e.targate.value
        this.setState({
            ...values,
            [name]: value
        });
    }

    return {handleChange, values};
}

export default helpForm;
