import React, { Component } from 'react'
import Select from 'react-select'
import countryList from 'react-select-country-list'
import SelectCurrency from 'react-select-currency'
import styles from './FormStyle.css'
import axios from 'axios'


const cList = countryList().getData()
const selectedCurrency = currency => {
    this.setState({currency})
}
const formErrors ={
    merchantError: '',
    merchantNameError:'',
    orderIdError:'',
    productError:'',
    priceError:'',
    currencyError: '',
    quantityError:'',
    customError: '',
    amountError:'',
    countryError: '',
    firstnameError:'',
    lastnameError:'',
    addressError: '',
    cityError: '',
    stateCError:'',
    postalcodeError: '',
    mobileError: '',
    emailError:'',
}
const forget = { country: null,
    firstname:'',
    lastname:'',
    address: '',
    city: '',
    stateC:'',
    postalcode: '',
    mobile: '',
    email: '',}
export default class Form extends Component{
    constructor(props) {
    super(props);
    this.state = 
    {
        merchant : '',
        mercahtName: '',
        orderId:'',
        product:'',
        price:'',
        currency: '',
        quantity:'',
        custom: '',
        amount:'',
        country: null,
        firstname:'',
        lastname:'',
        address: '',
        city: '',
        stateC:'',
        postalcode: '',
        mobile: '',
        email: '',
    }

    }

    isValid = (formErrors) => {
        let valid = false;
        Object.keys(formErrors).values.length > 0 ? valid = false : valid = true;
        return valid;
    }
    handleError=(e)=>{
        console.log("Handle error function called")
        console.log(formErrors)
    }
    handleSubmit=(event)=>{
        event.preventDefault();
        console.log("handleSummit method ")
        if(this.isValid(formErrors)){
            const postData = {
                merchant : {
                    merchant: this.state.merchant,
                    merchantName: this.state.merchantName,

                },
                order:{
                    orderId: this.state.orderId,
                    item_name:this.state.product,
                    item_price:this.state.price,
                    currency: this.state.currency,
                    quantity:this.state.quantity,
                    custom: this.state.custom,
                    amount:this.state.amount,
                },
                payer:{
                    country: this.state.country,
                    firstname: this.state.firstname,
                    lastname:this.state.lastname,
                    address: this.state.address,
                    city: this.state.city,
                    stateC:this.state.amount,
                    postalcode: '',
                    mobile: '',
                    email: '',
                }
            }

            axios.post(`https://sandbox.flocash.com/ecom/ecommerce.do`, postData)
                .then(res=>{
                    console.log(res)
                    console.log(res.data)
                }).catch(error => {
                        console.error('Catch an error!', error.message);
                    });
            console.log(postData)
        }   
        else{
            this.handleError(event)
        }
    }

    handleChange=(e)=>{
        e.preventDefault();
        const emailRegExp = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
        const passwordRegExp = RegExp(/^(?=.*\d)(?=.*[a-z])(?=.&[A-Z])[0-9a-zA-Z]{8,}$/)
        const currencyRegex = RegExp(/^(?=.*\d)(?=.&[A-Z])[A-Z]{3,}$/)
        const nameRegExp = RegExp(/^(?=.*\d)(?=.*[a-z])(?=.&[A-Z])[A-Z]{3,}$/)
        const numberRegExp = RegExp(/^(?=.*[0-9]){1,}$/)
        const mobileRegExp = RegExp(/^(\+[1-9]){1}[1-9]{1}[0-9]{9,14}$/)

        const {name, value} = e.target;
        switch(name){
            case "merchant": 
                formErrors.merchantError = nameRegExp.test(value) || value.length < 6 ? "minmum 6 charachters" : this.state.merchant = value;
            break;
            case "merchantName": 
                formErrors.merchantNameError = nameRegExp.test(value) || value.length < 3 ? "minmum 6 charachters" : this.state.merchantName = value;
            break;
            case "orderId": 
            formErrors.orderIdError = value.length < 4 ? "minmum 4 charachters" : this.state.orderId = value
            break;
            case "product": 
                formErrors.productError = value.length < 3 ? "minmum 3 charachters" : this.state.product = value;
            break;
            case "price": 
                formErrors.priceError = numberRegExp.test(value) || value.lenght < 1 ? "enter only numbers" : this.state.price = value;
            break;
            case "currency": 
                 formErrors.currencyError = currencyRegex.test(value) || value.length == 2 ? "maximum 2 upercased charachters" : this.state.currency = value;
            break;
            case "quantity": 
                formErrors.quantityError = numberRegExp.test(value) || value.length < 1 ? "only numbers" : this.state.quantity = value;
            break;
            case "custom": 
                 formErrors.customError = value.length < 4 ? "minmum 4 charachters" : this.state.custom = value;
            break;
            case "amount": 
                formErrors.amountError = numberRegExp.test(value) || value.length < 1 ? "only numbers" : this.state.amount = value;
            break;
            case "country": 
            formErrors.countryError = value.length = 2 ? "2 upper cased charachters" : "";
            break;

            case "firstname": 
                formErrors.firstnameError = nameRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" : this.state.country = value;
            break;

            case "lastname": 
                formErrors.lastnameError = nameRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" : this.state.firstname = value;
            break;
            case "mobile": 
                formErrors.lastnameError = nameRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" : this.state.lastname = value;
            break;
            case "address": 
                formErrors.addressError = nameRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" : this.state.address = value;
            break;
            case "city": 
                formErrors.cityError = nameRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" : this.state.city = value;
            break;
            case "stateC": 
                formErrors.stateCError = nameRegExp.test(value) && value.length < 3 ? "minmum 4 digit number" : this.state.stateC = value;
            break;
            case "postalcode": 
                formErrors.postalcodeError = numberRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" : this.state.postalcode = value;
            break;
            case "email": 
                formErrors.email = emailRegExp.test(value) && value.length < 3 ? "minmum 3 charachters" : this.state.email = value;
            break;
            case "mobile": 
            formErrors.mobile = mobileRegExp.test(value) && value.length < 3 ? "invalid mobile number" : this.state.mobile = value;
            break;

            default: 
            break;
        } 
    }
    selectedCurrency = (currency) => {
        this.state.currency = currency;
    }
    render(){
        const country = this.state.country;
        return (

            <div className="form-content">
               <form className="form"  onSubmit={this.handleSubmit}>
                    <h2 className="headline">Express Checkout </h2>
                    <h3 className="info">Merchant Info</h3>
                    <div>
                        <label htmlFor="merchant" className="form-lable">
                            <input type="text" name="merchant" className="form-input" required="true" placeholder="Merchant Account" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.merchantError != null ?(
                        <span className="from-error">{formErrors.merchantError}</span>
                       ):
                       null
                   }

                   <h3 className="info">Order Info</h3>

                   <div>
                        <label htmlFor="orderId" className="form-lable">
                            <input type="text" name="orderId" className="form-input" required="true" placeholder="Order Id" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.orderIdError != null ? (
                        <div className="from-error">{formErrors.orderIdError}</div>
                       ):
                       null
                   }
                   <div >
                        <label htmlFor="product" className="form-lable">
                            <input type="text" name="product" className="form-input" required="true" placeholder="Product" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.productError !=null ?(
                        <div className="from-error">{formErrors.productError}</div>
                       ):
                       null
                   }

                   <div>
                        <label htmlFor="price" className="form-lable">
                            <input type="number" name="price" className="form-input" required="true" placeholder="Price" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.priceError != null ?(
                        <div className="from-error">{formErrors.priceError}</div>
                       ):
                       null
                   }
                   <div>
                        <label htmlFor="quantity" className="form-lable">
                            <input type="number" name="quantity" className="form-input" required="true" placeholder="Quantity" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.quantityError != null ?(
                        <div className="from-error">{formErrors.quantityError}</div>
                       ):
                       null
                   }

                   <div>
                        <label htmlFor="custom" className="form-lable">
                            <input type="text" name="custom" className="form-input" required="true" placeholder="Custom" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.customError != null ?(
                        <div className="from-error">{formErrors.customError}</div>
                       ):
                       null
                   }

                   <div>
                        <label htmlFor="amount" className="form-lable">
                            <input type="number" name="amount" className="form-input" required="true" placeholder="Amount" onChange={this.handleChange}/>
                        </label>
                   </div>
                   {
                       formErrors.amountError != null ?(
                        <div className="from-error">{formErrors.amountError}</div>
                       ):
                       null
                   }
                    <div className="form-input-div">
                        <label htmlFor="currency" className="form-lable">  
                            Currency:
                            <SelectCurrency value={'USD'} onCurrencySelected={selectedCurrency} />

                        </label>
                   </div>
                   {
                       formErrors.currencyError != null?(
                        <div className="from-error">{formErrors.currencyError}</div>
                       ):
                       null
                   }

                    <h3 className="info">Payer Info</h3>

                    <div className="form-input-div">

                        <label htmlFor="country" className="form-lable">
                        <Select placeholder="---- select country ----" options={cList} value={country} onChange={this.handleSelectChange}/>
                        </label>
                    </div>
                    {
                        formErrors.countryError != null ?(
                        <div className="from-error">{formErrors.countryError}</div>
                        ):
                        null
                    }

                    <div>
                        <label htmlFor="firstname" className="form-lable">
                            <input type="text" name="firstname" className="form-input" required="true" placeholder="Firstname" onChange={this.handleChange}/>
                        </label>
                    </div>
                    {
                        formErrors.firstnameError != null ?(
                        <div className="from-error">{formErrors.firstnameError}</div>
                        ):
                        null
                    }

                    <div>
                        <label htmlFor="lastname" className="form-lable">
                            <input type="text" name="lastname" className="form-input" required="true" placeholder="Lastname" onChange={this.handleChange}/>
                        </label>
                    </div>
                    {
                        formErrors.lastnameError != null ?(
                        <div className="from-error">{formErrors.lastnameError}</div>
                        ):
                        null
                    }
                    <div>
                        <label htmlFor="addres" className="form-lable">
                            <input type="text" name="address" className="form-input" required="true" placeholder="Address" onChange={this.handleChange}/>
                        </label>
                    </div>
                    {
                        formErrors.addressError != null ?(
                        <div className="from-error">{formErrors.addressError}</div>
                        ):
                        null
                    }

                    <div>
                        <label htmlFor="city" className="form-lable">
                            <input type="text" name="city" className="form-input" required="true" placeholder="City" onChange={this.handleChange}/>
                        </label>
                    </div>
                    {
                        formErrors.cityError != null ?(
                        <div className="from-error">{formErrors.cityError}</div>
                        ):
                        null
                    }
                    <div>
                        <label htmlFor="stateC" className="form-lable">
                            <input type="text" name="sateC" className="form-input" required="true" placeholder="Sate" onChange={this.handleChange}/>
                        </label>
                    </div>
                    {
                        formErrors.sateCError != null ?(
                        <div className="from-error">{formErrors.sateCError}</div>
                        ):
                        null
                    }
                    <div >
                        <label htmlFor="postalcode" className="form-lable">
                            <input type="text" name="postalcode" className="form-input" required="true" placeholder="Postalcode" onChange={this.handleChange}/>
                        </label>
                    </div>
                    {
                        formErrors.postalcodeError != null ?(
                        <div className="from-error">{formErrors.postalcodeError}</div>
                        ):
                        null
                    }

                    <div>
                        <label htmlFor="mobile" className="form-lable">
                            <input type="tel" name="mobile" className="form-input" required="true" placeholder="Mobile" onChange={this.handleChange}/>
                        </label>
                    </div>
                    {
                        formErrors.mobileError != null?(
                        <div className="from-error">{formErrors.mobileError}</div>
                        ):
                        null
                    }

                    <div >
                        <label htmlFor="email" className="form-lable">
                            <input type="email" name="email" className="form-input" required="true" placeholder=" Email" onChange={this.handleChange}/>
                        </label>
                    </div>
                    {
                        formErrors.emailError != null ?(
                        <div className="from-error">{formErrors.emailError}</div>
                        ):
                        null
                    }                   


                   <button className="form-input-btn" type="submit" onSubmit={this.handleSubmit}>Checkout</button>
                </form>

            </div>
        )
    }


}
