import React,{Component} from 'react'
import Select from 'react-select'
import countryList from 'react-select-country-list'
import SelectCurrency from 'react-select-currency'



const cList = countryList().getData()
const selectedCurrency = currency => {
    this.setState({currency})
}
const defaultState={
        merchant : '',
        apiUsername:'',
        apiUserPassword:'',

        orderId:'',
        product:'',
        price:'',
        currency: '',
        quantity:'',
        custom: '',
        amount:'',

        country: null,
        firstname:'',
        lastname:'',
        mobile: '',
        email: '',
        //-------------------
        merchantError: '',
        apiUsernameError:'',
        apiUserPasswordError:'',

        orderIdError:'',
        productError:'',
        priceError:'',
        currencyError: '',
        quantityError:'',
        customError: '',
        amountError:'',

        countryError: null,
        firstnameError:'',
        lastnameError:'',
        mobileError: '',
        emailError:'',

}

export default class FormCheckout extends Component{
    state = defaultState

    emailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
    passwordRegex = RegExp(/^(?=.*\d)(?=.*[a-z])(?=.&[A-Z])[0-9a-zA-Z]{8,}$/)
    //currencyRegex = RegExp(/^(?=.*\d)(?=.&[A-Z])*{3}$/)
    validate = () =>{
        const checker = true;

        if(!this.state.merchant.match(/^[a-zA-Z0-9]*$/)){
            this.state.merchantError = "invalid merchant name"
            console.log(this.merchantError)
            this.setState({
            })
            checker = false
        }

        if(!this.state.apiUsername.match(/^[a-zA-Z0-9]*$/)){
            apiUsernameError = "invalid api user name"
            this.setState({apiUsernameError})

            checker = false
