import React,{Component, isValidElement} from 'react'
import Select from 'react-select'
import countryList from 'react-select-country-list'
import SelectCurrency from 'react-select-currency'



const cList = countryList().getData()
const selectedCurrency = currency => {
    this.setState({currency})
}
const defaultState={
        merchant : '',
        apiUsername:'',
        apiUserPassword:'',

        orderId:'',
        product:'',
        price:'',
        currency: '',
        quantity:'',
        custom: '',
        amount:'',

        country: null,
        firstname:'',
        lastname:'',
        mobile: '',
        email: '',
        //-------------------
        merchantError: '',
        apiUsernameError:'',
        apiUserPasswordError:'',

        orderIdError:'',
        productError:'',
        priceError:'',
        currencyError: '',
        quantityError:'',
        customError: '',
        amountError:'',

        countryError: null,
        firstnameError:'',
        lastnameError:'',
        mobileError: '',
        emailError:'',

}

export default class FormCheckout extends Component{
    state = defaultState
    emailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
    validate = () =>{
        let merchantError = ''
        let apiUsernameError = ''
        let apiUserPasswordError = ''

        let orderIdError = ''
        let productError =''
        let priceError = '' 
        let currencyError = ''
        let quantityError = ''
        let customError = ''
        let amountError = ''

        let countryError = null
        let firstnameError =''
        let lastnameError = ''
        let mobileError = ''
        let emailError = ''

        if(!this.state.merchant.match(/^[a-zA-Z0-9]*$/)){
            merchantError = "invalid merchant name"
            this.setState({merchantError})
            return false;
        }
        if(!this.state.orderId.match(/^[0-9]*$/)){
            orderIdError = "invalid merchant name"
            this.setState({orderIdError})
            return false;
        }
        if(!this.state.apiUsername.match(/^[a-zA-Z0-9]*$/)){
            apiUsernameError = "invalid api user name"
            this.setState({apiUsernameError})
            return false;
        }
        if(!this.state.apiUserPassword.match(/^[a-zA-Z]+$/)){
            apiUserPasswordError = "password should at least have 8 characters, one capital letter, and one number"
            this.setState({merchantError})
            return false;
        }

        if(!this.state.email.includes('@'),'.'){
            emailError = "invalid email"
            this.setState({emailError})
            return false;
        }
        switch (defaultState.it) {
            case 'fullName': 
              errors.fullName = 
                value.length < 5
                  ? 'Full Name must be 5 characters long!'
                  : '';
              break;
            case 'email': 
              errors.email = 
                validEmailRegex.test(value)
                  ? ''
                  : 'Email is not valid!';
              break;
