import React,{Component} from 'react'
import Select from 'react-select'
import countryList from 'react-select-country-list'
import SelectCurrency from 'react-select-currency'
import styles from './Form.css'
import axios from 'axios'


const flocashApi = axios.create({
baseURL:`http://sandbox.flocash.com/ecom/ecommerce.do`
});
const cList = countryList().getData()
const selectedCurrency = currency => {
    this.setState({currency})
}

export default class FormCheckout extends Component{
    constructor(props) {
    super(props);

    this.state = 
    {
        merchant : '',
        apiUsername:'',
        apiUserPassword:'',

        orderId:'',
        product:'',
        price:'',
        currency: '',
        quantity:'',
        custom: '',
        amount:'',

        country: null,
        firstname:'',
        lastname:'',
        mobile: '',
        email: '',
    //-------------------
        formErrors:  {
            merchantError: '',
            apiUsernameError:'',
            apiUserPasswordError:'',

            orderIdError:'',
            productError:'',
            priceError:'',
            currencyError: '',
            quantityError:'',
            customError: '',
            amountError:'',

            countryError: '',
            firstnameError:'',
            lastnameError:'',
            mobileError: '',
            emailError:'',
        }
    }

    }

    isValid = (formErrors) => {
        let valid = false;
        Object.keys(formErrors).values.length > 0 ? valid = false : valid = true;
        return valid;
    }
    handleSubmit=(event)=>{
        event.preventDefault();
        console.log("hello handleSummit() method ")
        if(this.isValid(this.state.formErrors)){
