import React, { Component } from 'react'
import Select from 'react-select'
import countryList from 'react-select-country-list'
import SelectCurrency from 'react-select-currency'
import styles from './FormStyle.css'
import axios from 'axios'



const flocashApi = axios.create({
baseURL:`http://sandbox.flocash.com/ecom/ecommerce.do`
});
const cList = countryList().getData()
const selectedCurrency = currency => {
    this.setState({currency})
}

export default class Form extends Component{
    constructor(props) {
    super(props);

    this.state = 
    {
        merchant : '',
        merchantName:'',
        orderId:'',
        product:'',
        price:'',
        currency: '',
        quantity:'',
        custom: '',
        amount:'',
        country: null,
        firstname:'',
        lastname:'',
        city: '',
        mobile: '',
        email: '',
        formErrors:  {
            merchantError: '',
            merchantNameError:'',
            orderIdError:'',
            productError:'',
            priceError:'',
            currencyError: '',
            quantityError:'',
            customError: '',
            amountError:'',
            countryError: '',
            firstnameError:'',
            lastnameError:'',
            mobileError: '',
            emailError:'',
        }
    }

    }

    isValid = (formErrors) => {
        let valid = false;
        Object.keys(formErrors).values.length > 0 ? valid = false : valid = true;
        return valid;
    }
    handleError=(e)=>{

    }
    handleSubmit=(event)=>{
        event.preventDefault();
        console.log("hello handleSummit() method ")
        if(this.isValid(this.state.formErrors)){
            console.log(this.state)
            const checkoutToApi = flocashApi.post(this.state).then(res=>{
