import React,{Component} from 'react'
import Select from 'react-select'
import countryList from 'react-select-country-list'
import SelectCurrency from 'react-select-currency'



const cList = countryList().getData()
const selectedCurrency = currency => {
    this.setState({currency})
}



export default class FormCheckout extends Component{
    //state = defaultState

    constructor(props) {
        super(props);
        this.state = {
            merchant : '',
            apiUsername:'',
            apiUserPassword:'',

            orderId:'',
            product:'',
            price:'',
            currency: '',
            quantity:'',
            custom: '',
            amount:'',

            country: null,
            firstname:'',
            lastname:'',
            mobile: '',
            email: '',
        //-------------------
            formErors:  {
                merchantError: '',
                apiUsernameError:'',
                apiUserPasswordError:'',

                orderIdError:'',
                productError:'',
                priceError:'',
                currencyError: '',
                quantityError:'',
                customError: '',
                amountError:'',

                countryError: null,
                firstnameError:'',
                lastnameError:'',
                mobileError: '',
                emailError:'',
            }
    }
    //to keep the state
    // this.handleSubmit = this.handleSubmit.bind(this)   

    isValid = (formErrors) => {

        let valid = true;
        Object.values(formErrors).forEach( errors => {
            (errors.length >= 0) && (valid = false)
        } );

        return valid;
    }

    handleSubmit=(event)=>{
        event.preventDefault();
        console.log("hello handleSummit() method ")
        //const isValid = this.validate;
        if(this.isValid(this.state.formErors)){
