    handleChange=(e)=>{
        e.preventDefault();
        console.log(this.state)
        const emailRegExp = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
        const passwordRegExp = RegExp(/^(?=.*\d)(?=.*[a-z])(?=.&[A-Z])[0-9a-zA-Z]{8,}$/)
        const currencyRegex = RegExp(/^(?=.*\d)(?=.&[A-Z])[A-Z]{3,}$/)
        const nameRegExp = RegExp(/^(?=.*\d)(?=.*[a-z])(?=.&[A-Z])[A-Z]{3,}$/)
        const {name, value} = e.target;
        let formErrors =  this.state.formErrors;

        switch(name){
            case "merchant": 
                formErrors.mearachantError = nameRegExp.test(value) || value.length < 3 ? "minmum 3 charachters" : "";
            break;

            case "apiUsername": 
                formErrors.apiUsernameError = nameRegExp.test(value) || value.length < 3 ? "minmum 3 charachters" : "";
            break;

            case "apiUserPassword": 
                 formErrors.apiUserPasswordError = passwordRegExp.test(value) || value.length < 1 ? "minmum 8 charachters" : "";
            break;

            case "orderId": 
            formErrors.orderIdError = value.length < 4 ? "minmum 4 charachters" : "";
            break;

            //___________
            case "product": 
                formErrors.productError = value.length < 3 ? "minmum 3 charachters" : "";
            break;

            case "price": 
                formErrors.priceError = value.length < 3 ? "minmum 3 charachters" : "";
            break;

            case "currency": 
                 formErrors.currencyError = currencyRegex.test(value) || value.length == 2 ? "maximum 2 upercased charachters" : "";
            break;

            case "quantity": 
            formErrors.quantityError = value.length < 4 ? "minmum 4 charachters" : "";
            break;
            case "custom": 
                 formErrors.customError = value.length < 4 ? "minmum 4 charachters" : "";
            break;

            case "amount": 
            formErrors.amountError = value.length < 0 ? "minmum 1 amount" : "";
            break;

            //____________________
            case "country": 
                formErrors.countryError = value.length = 2 ? "2 upper cased charachters" : "";
            break;

            case "firstname": 
                formErrors.firstnameError = nameRegExp.test(value) || value.length < 3 ? "minmum 3 charachters" : "";
            break;

            case "lastname": 
                formErrors.lastnameError = nameRegExp.test(value) || value.length < 3 ? "minmum 3 charachters" : "";
            break;
            case "mobile": 
                formErrors.lastnameError = nameRegExp.test(value) || value.length < 3 ? "minmum 3 charachters" : "";
            break;
            case "email": 
                formErrors.email = emailRegExp.test(value) || value.length < 3 ? "minmum 3 charachters" : "";
            break;

            default: 
            break;
        }
        this.setState({formErrors, [name]: value}, ()=>{
            console.log(this.state)
        });
        }

    }

